import { Activity } from "../types/Activity";
import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

interface LoginFormProps {
  onLogin: (
    username: string,
    role: string,
    activities: Activity[],
    password: string
  ) => void;
}

function LoginForm(props: LoginFormProps): JSX.Element {
  const [username, setUsername] = useState<string>('');
  const [password, setPassword] = useState<string>('');
  const [error, setError] = useState<string>('');

  function handleSubmit(event: React.FormEvent) {
    event.preventDefault();
    console.log(username, password);

    // Skicka användardata till din Mirage-backend för att verifiera användaren
    axios
      .post('/login', { username, password })
      .then((response) => {
        const user = response.data;
        if (user) {
          console.log('Logged in as:', user.name);

          // Anropa onLogin-funktionen för att utföra inloggningen
          props.onLogin(user.name, user.role, user.activities, password);

          setError('');
        } else {
          setError('Invalid username or password');
        }
      })
      .catch((error) => {
        console.error('Error logging in:', error);
        setError('Invalid username or password');
      });
  }


  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button type="submit">Log in!</button>
    </form>
  );
}

export default LoginForm;
