import React, { useState } from 'react';
import axios from 'axios'; // Om Axios inte redan är installerat, installera det med "npm install axios"
import { Activity } from '../types/Activity';

interface LoginFormProps {
  onLogin: (username: string, role: string, activities: Activity[], password: string) => void;
}

function LoginForm(props: LoginFormProps): JSX.Element {
  const [username, setUsername] = useState<string>('');
  const [password, setPassword] = useState<string>('');
  const [error, setError] = useState<string>('');

  function handleSubmit(event: React.FormEvent) {
    event.preventDefault();

    // Skicka en GET-förfrågan till Mirage-databasen för att söka efter användaren
    axios
      .get('/user', { params: { username, password } })
      .then((response) => {
        const user = response.data;
        console.log('Logged in as:', user.name);

        // Anropa onLogin-funktionen för att utföra inloggningen
        props.onLogin(user.name, user.role, user.activities, password);
        setError('');
      })
      .catch((error) => {
        console.error('Error logging in:', error);
        setError('Invalid username or password');
      });
  }

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        required
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
      />
      <button type="submit">Log in</button>
      {error && <p>{error}</p>}
    </form>
  );
}

export default LoginForm;
