import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Activity } from '../types/Activity';
import BookingButton from '../components/BookingButton';


interface BookinPageProps{
  activities: Activity[];
  setActivities: React.Dispatch<React.SetStateAction<Activity[]>>;
}
function BookingPage(props:BookinPageProps): JSX.Element {
  //const [activities, setActivities] = useState<Activity[]>([]);
  const { activities, setActivities} = props;
  const [filteredActivities, setFilteredActivities] = useState<Activity[]>([]);


  useEffect(() => {
    // Fetch activities from your Mirage.js server
    axios.get<Activity[]>('/activities') // Assuming your Mirage.js server responds to '/activities' route
      .then((response) => {
        setActivities(response.data);
        setFilteredActivities(response.data.filter((activity) => activity.maxCount > 0));
      })
      .catch((error) => {
        console.error('Error fetching activities:', error);
      });
  }, []);

  return (
    <div>
      <h2>BookingPage</h2>
      <h3>Activities:</h3>
      <ul>
        {filteredActivities.map((activity) => (
          <li key={activity.id}>
            <h4>{activity.title}</h4>
            <p>{activity.content}</p>
            <p>Date: {new Date(activity.date).toLocaleDateString('en-GB')}</p>
            <p>Max Count: {activity.maxCount}</p>
            {/* Pass the activity as a prop to BookingButton */}
            <BookingButton activity={activity} />
          </li>
        ))}
      </ul>
      <br />
      <br />
      {activities.map((activity)=>(
        <li key={activity.id}>
          <h2>{activity.title}</h2>
        </li>
      ))}
    </div>
  );
}

export default BookingPage;
