import React from 'react';
import AddActivityForm from '../components/AddActivityForm';
import { Activity } from '../types/Activity';
import { Link } from 'react-router-dom';

interface AddActivityPageProps {
  activities: Activity[];
  setActivities: React.Dispatch<React.SetStateAction<Activity[]>>;
}

function AddActivityPage(props: AddActivityPageProps): JSX.Element {
  const { activities, setActivities } = props;

  // Funktionen för att hantera tillägg av en ny aktivitet
  function onAdd(id: number, title: string, content: string, date: string, maxCount: number) {
    // Skapa en ny aktivitet med de givna parametrarna
    const newActivity: Activity = {
      id,
      title,
      content,
      date,
      maxCount,
    };

    // Skapa en kopia av den befintliga activities-arrayen och lägg till den nya aktiviteten
    const updatedActivities = [...activities, newActivity];

    // Uppdatera activities-arrayen med den nya arrayen
    setActivities(updatedActivities);
  }

  return (
    <div>
      <h1>Add Activity</h1>
      <Link to="/admin">Back to Admin</Link>

      {/* Rendera AddActivityForm och skicka med funktionen onAdd */}
      <AddActivityForm onAdd={onAdd} />

      {/* Rendera listan med aktiviteter */}
      <h2>Activities:</h2>
      <ul>
        {activities.map((activity) => (
          <li key={activity.id}>
            <h3>{activity.title}</h3>
            <p>{activity.content}</p>
            <p>Date: {activity.date}</p>
            <p>Max Count: {activity.maxCount}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AddActivityPage;
